import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Animated } from 'react-native';
import styled from 'styled-components/native';

// Create an Animated value for smooth transitions
const fadeAnim = new Animated.Value(0);

const App = () => {
  // Start the animation when the component mounts
  React.useEffect(() => {
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 2000,  // Set the animation duration
      useNativeDriver: true,  // Use native driver for better performance
    }).start();
  }, []);

  return (
    <Container>
      <Animated.View style={[styles.header, { opacity: fadeAnim }]}>
        <Text style={styles.heading}>Welcome to My Personal Page</Text>
        <Text style={styles.subtitle}>Here are some useful links:</Text>
      </Animated.View>
      <LinksContainer>
        <LinkButton onPress={() => window.open('https://www.linkedin.com/in/your-profile', '_blank')}>
          <LinkText>LinkedIn</LinkText>
        </LinkButton>
        <LinkButton onPress={() => window.open('https://github.com/your-username', '_blank')}>
          <LinkText>GitHub</LinkText>
        </LinkButton>
        <LinkButton onPress={() => window.open('/resume.pdf', '_blank')}>
          <LinkText>Resume</LinkText>
        </LinkButton>
      </LinksContainer>
    </Container>
  );
};

const Container = styled.View`
  flex: 1;
  justify-content: center;
  align-items: center;
  background-color: #f4f4f9;
  padding: 20px;
`;

const LinksContainer = styled.View`
  margin-top: 30px;
`;

const LinkButton = styled.TouchableOpacity`
  background-color: #007BFF;
  padding: 12px 25px;
  margin: 10px;
  border-radius: 8px;
`;

const LinkText = styled.Text`
  color: white;
  font-size: 18px;
  font-weight: bold;
`;

const styles = StyleSheet.create({
  header: {
    textAlign: 'center',
    marginBottom: 20,
  },
  heading: {
    fontSize: 30,
    fontWeight: 'bold',
    color: '#333',
  },
  subtitle: {
    fontSize: 18,
    color: '#666',
  },
});

export default App;
